        '',
        'UPDATE ca_objects_x_object_representations SET representation_id = ',
        original_representation_id,
        ' WHERE relation_id = ',
        new_relation_id, ';'
    ) AS update_sql
FROM object_image_relinking
WHERE original_deleted;
DROP VIEW IF EXISTS object_image_relinking;

